import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios
import './comp1.css';
import { TailSpin } from 'react-loader-spinner';

const Component1 = ({ changeId, onSubmit }) => {
  const [formData, setFormData] = useState({
    project: '',
    project_type: '',
    project_role: '',
    organization_name: '',
    manage: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isProjectRegistered, setIsProjectRegistered] = useState('');

  const checkProjectRegistration = async () => {
    setIsLoading(true); // Start loading spinner

    try {
      const response = await axios.post('http://localhost:3001/check-project', {
        project: formData.project,
      });

      const data = response.data;
      setIsProjectRegistered(data.details); // Assuming details contain the project name
    } catch (error) {
      console.error('Error checking project registration:', error);
    }

    setIsLoading(false); // Stop loading spinner
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      await checkProjectRegistration();
      setIsLoading(false);
      if (isProjectRegistered !== formData.project) {
        changeId(2);
        onSubmit(formData);
      } else {
        setIsProjectRegistered("Project Exists");
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());
  }, [formData]);

  return (
    <form id="componentForm" className="component1">
      <h2>Create a project</h2>
      <br />
      <div className='label-container'>
        <label>
          <h3>Name</h3><br/>
          <input
            className="input-box"
            type="text"
            name="project"
            value={formData.project}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          <h3>Type</h3><br/>
          <select
            className="select-box"
            name="project_type"
            value={formData.project_type}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select a type of project
            </option>
            <option value="User Application">User Application</option>
            <option value="SDK">SDK</option>
            <option value="Protocol Stack">Protocol Stack</option>
          </select>
        </label>
      </div>

      <div className='label-container'>
        <label>
          <h3>Select Your Role</h3><br/>
          <select
            className="select-box"
            name="project_role"
            value={formData.project_role}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select your role
            </option>
            <option value="BAP">BAP</option>
            <option value="BPP">BPP</option>
            <option value="Beckn Gateway">Beckn Gateway</option>
            <option value="Beckn Registry">Beckn Registry</option>
          </select>
        </label>

        <label>
          <h3>Organization Name</h3><br/>
          <input
            className="input-box"
            type="text"
            name="organization_name"
            value={formData.organization_name}
            onChange={handleInputChange}
            required
          />
        </label>
      </div>

      {/* Manage Option */}
      <div className="button-group">
  <button  className='green'  onClick={() => handleInputChange({ target: { name: 'manage', value: 'yes' } })}
  >
    Yes
  </button>
  <button
    className={`option-button ${formData.manage === 'no' ? 'selected-no' : 'red'} `}
    style={{ borderColor: formData.manage === 'no' ? 'red' : 'green' }}
    onClick={() => handleInputChange({ target: { name: 'manage', value: 'no' } })}
  >
    No
  </button>
</div>

      <button className="save-button" onClick={handleSubmit} disabled={!isFormValid}>
        Save and Proceed
      </button>

      {isLoading && (
        <TailSpin type="ThreeDots" color="#00BFFF" height={50} width={50} />
      )}

      <p>{isProjectRegistered}</p>
    </form>
  );
};

export default Component1;
