            setClicked(false);
        }, 1000); // Reset after 1 second
    };

    return (
        <div className="apicomponent-box">
            <h1 className='project-name'>{project}</h1>
            <div className={`api-container ${clicked ? 'clicked' : ''}`}>
                <h1 className='project-api' onClick={handleCopyClick}>
                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
                    <button className="copy-button">{clickName}</button>
                </h1>
            </div>
            <div className='apirole-container'>
                <p className='api-domain'>{funnel_stages}</p>
                <p className='api-role'>{project_role}</p>
                <FontAwesomeIcon icon={faEdit} className='project-edit' />
            </div>
        </div>
    );
};

export default ApiComponent;
