import React, { useState, useEffect } from 'react';
import './comp1.css';
import { TailSpin } from 'react-loader-spinner';

const Component1 = ({ changeId, onSubmit }) => {
  const [formData, setFormData] = useState({
    project: '',
    project_type: '',
    project_role: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isProjectRegistered, setIsProjectRegistered] = useState(false); // Changed state variable name

  const checkProjectRegistration = async () => {
    const queryParams = `?project=${formData.project}`;
    const response = await fetch(`http://localhost:3001/name${queryParams}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const data = await response.json();
    const { update } = data;
    setIsProjectRegistered(update.length > 0); // Corrected line
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      setIsLoading(true);
      await checkProjectRegistration();
      if (!isProjectRegistered) {
        changeId(2);
        onSubmit(formData);
      }
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());
  }, [formData]);

  return (
    <form id="componentForm" className="component1">
      <h2>Create a project</h2>
      <br />
      <label>
        <h3>Name</h3>
        <input
          className="input-box"
          type="text"
          name="project"
          value={formData.project}
          onChange={handleInputChange}
          required
        />
      </label>

    <label>
      <h3>Type</h3>
      <select
        className="select-box"
        name="project_type"
        value={formData.project_type}
        onChange={handleInputChange}
        required
      >
        <option value="" disabled hidden>
          Select a type of project
        </option>
        <option value="User Application">User Application</option>
        <option value="SDK">SDK</option>
        <option value="Protocol Stack">Protocol Stack</option>
      </select>
    </label>

    <label>
      <h3>Select Your Role</h3>
      <select
        className="select-box"
        name="project_role"
        value={formData.project_role}
        onChange={handleInputChange}
        required
      >
        <option value="" disabled hidden>
          Select your role
        </option>
        <option value="BAP">BAP</option>
        <option value="BPP">BPP</option>
        <option value="Beckn Gateway">Beckn Gateway</option>
        <option value="Beckn Registry">Beckn Registry</option>
      </select>
    </label>

    <button className="save-button" onClick={handleSubmit} disabled={!isFormValid}>
      Save and Proceed
    </button>

    {isLoading && (
      <TailSpin type="ThreeDots" color="#00BFFF" height={50} width={50} />
    )}

    {isProjectRegistered && (
      <p>Project Name is already registered.</p>
    )}
  </form>
  );
};

export default Component1;
