import React, { useState } from 'react';
import Component1 from './Component1/comp1';
import Component2 from './Component2/comp2';
import Component3 from './Component3/comp3';
import Component4 from './Component4/comp4';
import './wizard.css';
import { v4 as uuidv4 } from 'uuid';

function Wizard(props) {
  const {userData} = props
  const {email, organization} = userData
  const [activeComponent, setActiveComponent] = useState(1);
  const [formData, setFormData] = useState({});

  const changeActiveComponent = (num) => {
    setActiveComponent(num);
  };

  const handleFormSubmit = (data) => {
    setFormData((prevData) => ({
      ...prevData,
      ...data,
    }));

    if (activeComponent === 3) {
      addProjectToDatabase();
    }
  };

  const addProjectToDatabase = async () => {
    try {
      const request = { ...formData, id: uuidv4() };
      const response = await fetch('http://localhost:3001/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Handle the response from the backend server, if needed
      const responseData = await response.json();
      alert('Added Project Successfully');
      console.log('Response from server:', responseData);

      setActiveComponent(1);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className='wizard-container'>
      <h1 className="mc-heading">Build with Beckn</h1>
      <div>
        {activeComponent === 1 && <Component1 changeId={changeActiveComponent} onSubmit={handleFormSubmit} org={organization} />}
        {activeComponent === 2 && <Component2 changeId={changeActiveComponent} onSubmit={handleFormSubmit} />}
        {activeComponent === 3 && <Component3 changeId={changeActiveComponent} onSubmit={handleFormSubmit} />}
        {activeComponent === 4 && <Component4 changeId={changeActiveComponent} />}
      </div>
    </div>
  );
}

export default Wizard;
