import React, { useState, useEffect } from 'react';
import './comp1.css';
import {TailSpin }from 'react-loader-spinner';

const Component1 = ({ changeId, onSubmit }) => {
  const [formData, setFormData] = useState({
    project: '',
    project_type: '',
    project_role: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isProjectRegistered, setIsProjectRegistered] = useState(false);
  const [isLoading, setIsLoading] = useState(false); // New state for loading

  const checkProjectRegistration = async () => {
    const queryParams = `?project=${formData.project}`;
    const response = await fetch(`http://localhost:3001/name${queryParams}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const data = await response.json();
    const { update } = data;
    setIsProjectRegistered(data && update.length > 0);
    setIsLoading(false); // Stop the loader when the response comes back
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      setIsLoading(true); // Start the loader when form is submitted
      await checkProjectRegistration();
      if (!isProjectRegistered) {
        changeId(2);
        onSubmit(formData);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());
  }, [formData]);

  return (
    <form id="componentForm" className="component1">
      <h2>Create a project</h2>
      <br />
      <label>
        <h3>Name</h3>
        <input
          className="input-box"
          type="text"
          name="project"
          value={formData.project}
          onChange={handleInputChange}
          required
        />
      </label>

      {/* ... Other form elements ... */}

      <button className="save-button" onClick={handleSubmit} disabled={!isFormValid}>
        Save and Proceed
      </button>

      {isLoading && (
        <TailSpin type="ThreeDots" color="#00BFFF" height={50} width={50} /> // Display the loader conditionally
      )}

      {isProjectRegistered && <p>Project Name is already registered.</p>}
    </form>
  );
};

export default Component1;
