import React, { Component } from 'react';
import Component1 from './Component1/comp1';
import Component2 from './Component2/comp2';
import Component3 from './Component3/comp3';
import Component4 from './Component4/comp4';
import './index.css';
import { v4 as uuidv4 } from 'uuid';

class MainContent extends Component {
  state = { activeComponent: 1, formData: {} };

  changeActiveComponent = (num) => {
    this.setState({ activeComponent: num });
  };

  handleFormSubmit = (data) => {
    const { activeComponent } = this.state;
    this.setState((prevState) => ({
      formData: { ...prevState.formData, ...data }
    }));

    if (activeComponent === 3) {
      this.addProjectToDatabase();
    }
  };

  addProjectToDatabase = async () => {
    try {
      const { formData } = this.state;
      const request = { ...formData, id: uuidv4() };
      const response = await fetch('http://localhost:3001/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(request)
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Handle the response from the backend server, if needed
      const responseData = await response.json();
      alert('Added Project Successfully');
      console.log('Response from server:', responseData);

      this.setState({ activeComponent: 1 });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  render() {
    const { activeComponent } = this.state;
    return (
      <div className="main-container">
        <h1 className="mc-heading">Build with Beckn</h1>
        {activeComponent === 1 && <Component1 changeId={this.changeActiveComponent} onSubmit={this.handleFormSubmit} />}
        {activeComponent === 2 && <Component2 changeId={this.changeActiveComponent} onSubmit={this.handleFormSubmit} />}
        {activeComponent === 3 && <Component3 changeId={this.changeActiveComponent} onSubmit={this.handleFormSubmit} />}
        {activeComponent === 4 && <Component4 changeId={this.changeActiveComponent} />}
      </div>
    );
  }
}

export default MainContent;
