import React, { useState, useRef, useEffect } from 'react';
import './navbar.css';
import ls from 'local-storage';
import { useNavigate } from 'react-router-dom';

const Navbar = (props) => {
  const { userData } = props;
  const { username } = userData;
  const letter = username[0];
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  const handleDropdownClick = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const logOutUser = () => {
    ls.remove('UserData');
    navigate('/signin/');
  }

  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownVisible(false);
      }
    };

    document.addEventListener('click', handleOutsideClick);

    return () => {
      document.removeEventListener('click', handleOutsideClick);
    };
  }, []);

  return (
    <nav className="flex items-center justify-between bg-[#333] text-[white] p-2.5">
      <div className="text-2xl pl-8">BIAB</div>
      <div className="w-[70%] p-2.5">
        <input
          type="text"
          placeholder="Search for people, communities, code and more..."
          className="w-full bg-[#f1f1f1] p-2 p-4 rounded-[20px] border-[none] outline-0"
        />
      </div>
      <div className="flex items-center">
        <button
          type="button"
          className="bg-[lightcoral] text-white cursor-pointer mr-4 px-8 py-4 rounded-[15px] border-0 active:scale-[0.8]"
          onClick={logOutUser}
        >
          Logout
        </button>
        <div className="w-10 h-10 bg-[#ccc] mr-2.5 rounded-[50%]">
          <p className="text-[2.2rem] m-0">{letter}</p>
        </div>
        <div
          className={`relative ${dropdownVisible ? 'active' : ''}`}
          ref={dropdownRef}
          onClick={handleDropdownClick}
        >
          <span className="cursor-pointer text-2xl">&#9776;</span>
          <div className="hidden absolute bg-[#f9f9f9] text-[#333] z-[1] p-2.5 rounded-[5px] right-0 top-full">
            <p>Name</p>
            <p>Email</p>
            <p>Logout</p>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
