import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './comp1.css';
import { TailSpin } from 'react-loader-spinner';

const Component1 = ({ onSubmit, org }) => {
  const [formData, setFormData] = useState({
    project: '',
    project_type: '',
    project_role: '',
    organization_name: org,
    industry_domains: 'Retail',
    funnel_stages: '',
    manage: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isProjectRegistered, setIsProjectRegistered] = useState('');
  const [isManage, setManage] = useState(true);

  const checkProjectRegistration = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/check-project', {
        project: formData.project,
      });

      const data = response.data;
      const { details } = data;
      return details;
    } catch (error) {
      console.error('Error checking project registration:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      const result = await checkProjectRegistration();
      if (result !== formData.project) {
        onSubmit(formData);
        generateEmptyFormData();
      } else {
        setIsProjectRegistered("Project Exists");
      }
    }
  };

  const generateEmptyFormData = () => ({
    project: '',
    project_type: '',
    project_role: '',
    organization_name: '',
    industry_domains: '',
    funnel_stages: '',
    manage: '', // Default value added
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());

    // Check if all fields are filled to hide the "Fill in the details" message
    const isAllFieldsFilled = Object.values(formData).every(value => value !== '');
    setManage(isAllFieldsFilled);
  }, [formData]);

  return (
    <form id="componentForm" className="component1" onSubmit={handleSubmit}>
      {/* ... (rest of the JSX) ... */}

      <button className="save-button" disabled={!isFormValid}>
        Save and Proceed
      </button>

      {isLoading && (
        <TailSpin type="ThreeDots" color="#00BFFF" height={50} width={50} />
      )}

      {!isFormValid && isManage && (
        <p>Fill in the details</p>
      )}

      <p>{isProjectRegistered}</p>
    </form>
  );
};

export default Component1;
