import React, { Component } from 'react';
import Component1 from './Component1/comp1';
import Component2 from './Component2/comp2';
import './index.css'
// Add more components as needed...

class MainContent extends Component {
  state = { activeComponent: 1 };

  changeActiveComponent = (num) => {
    this.setState(prev => ({ activeComponent: num }));
  };


  renderActiveComponent = () => {
    const { activeComponent } = this.state;

    switch (activeComponent) {
      case 1:
        return <Component1 changeId={this.changeActiveComponent} />;
      case 2:
        return <Component2 changeId={this.changeActiveComponent} />;
      default:
        return null;
    }
  };

  render() {
    return (
      <div className="main-container">
        <h1 className="mc-heading">Build with Beckn</h1>
        {this.renderActiveComponent()}
      </div>
    );
  }
}

export default MainContent;
