import React, { useState } from 'react';
import './comp3.css';

const Component3 = (props) => {
  const { changeId } = props;
  const [selectedOption, setSelectedOption] = useState('');
  const [setState] = useState({
    manage: null
  });

  const prevComponent = () => {
    changeId(2);
  };

  const nextComponent = () => {
    if (selectedOption === 'yes' || selectedOption === 'no') {
      changeId(3);
    }
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setState((prevState) => ({
      ...prevState,
      manage: option === 'yes' ? 1 : 0
    }));
  };
  const handleSubmit = () => {
    props.onSubmit({ manage: selectedOption === 'yes' ? 1 : 0 });
  };

  return (
    <div className="component3">
      <h2>Create a project</h2>
      <br />
      <div className="button-group">
        <button
          className={`option-button ${selectedOption === 'yes' ? 'selected-yes' : 'yes'}`}
          onClick={() => handleOptionClick('yes')}
        >
          Yes
        </button>
        <button
          className={`option-button ${selectedOption === 'no' ? 'selected-no' : 'no'}`}
          onClick={() => handleOptionClick('no')}
        >
          No
        </button>
      </div>
      <button className="prev-button" onClick={prevComponent}>
        Previous
      </button>
      <button className="save-button" onClick={handleSubmit} disabled={!selectedOption}>
        Submit
      </button>
    </div>
  );
};

export default Component3;
