import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './comp1.css';
import { TailSpin } from 'react-loader-spinner';

const Component1 = ({onSubmit, org }) => {
  const [formData, setFormData] = useState({
    project: '',
    project_type: '',
    project_role: '',
    organization_name: org,
    industry_domains: 'Retail',
    funnel_stages: '',
    manage: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isProjectRegistered, setIsProjectRegistered] = useState('');
  const [isManage, setManage] = useState(true);

  const checkProjectRegistration = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/check-project', {
        project: formData.project,
      });

      const data = response.data;
      const { details } = data;
      return details;
    } catch (error) {
      console.error('Error checking project registration:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      const result = await checkProjectRegistration();
      if (result !== formData.project) {
        // changeId(2);
        onSubmit(formData);
        generateEmptyFormData();
      } else {
        setIsProjectRegistered("Project Exists");
      }
    }
  };
  const generateEmptyFormData = () => ({
    project: '',
    project_type: '',
    project_role: '',
    organization_name: '',
    industry_domains: '',
    funnel_stages: '',
    manage: '', // Default value added
  });
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());
  }, [formData]);

  return (
    <form id="componentForm" className="component1" onSubmit={handleSubmit}>
      <h2>Create a project</h2>
      <br />
      <div className='label-container'>
        <label>
          <h3>Name</h3><br/>
          <input
            className="input-box"
            type="text"
            name="project"
            value={formData.project}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          <h3>Type</h3><br/>
          <select
            className="select-box"
            name="project_type"
            value={formData.project_type}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select a type of project
            </option>
            <option value="User Application">User Application</option>
            <option value="SDK">SDK</option>
            <option value="Protocol Stack">Protocol Stack</option>
          </select>
        </label>
      </div>

      <div className='label-container'>
        <label>
          <h3>Select Your Role</h3><br/>
          <select
            className="select-box"
            name="project_role"
            value={formData.project_role}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select your role
            </option>
            <option value="BAP">BAP</option>
            <option value="BPP">BPP</option>
            <option value="Beckn Gateway">Beckn Gateway</option>
            <option value="Beckn Registry">Beckn Registry</option>
          </select>
        </label>

        <label>
          <h3>Organization Name</h3><br/>
          <input
            className="input-box"
            type="text"
            name="organization_name"
            value={formData.organization_name}
            onChange={handleInputChange}
            required
          />
        </label>
      </div>
      <div className='label-container'>
        <label>
          <h3>Industry Domain</h3><br/>
          <select
            className="select-box"
            name="industry_domains"
            value={formData.industry_domain}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select Industry Domain
            </option>
            <option value="Retail">Retail</option>
            <option value="Logistics">Logistics</option>
            <option value="Healthcare">Healthcare</option>
            <option value="Mobility">Mobility</option>
          </select>
        </label>

        <label>
          <h3>Funnel Stages</h3><br/>
          <select
            className="select-box"
            name="funnel_stages"
            value={formData.funnel_stages}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled hidden>
              Select Funnel Stages
            </option>
            <option value="Package 1">Package 1</option>
            <option value="Package 2">Package 2</option>
            <option value="Package 3">Package 3</option>
            <option value="Package 4">Package 4</option>
          </select>
        </label>
      </div>

      <h3>Do you want to manage ?</h3>
      <div className="button-group">
        <button
          type="button"
          name="manage"
          value="1"
          className={`option-button yes ${formData.manage === '1' ? 'selected-yes' : 'unselected'}`}
          onClick={() => {handleInputChange, setManage(false)}}
        >
          Yes
        </button>
        <button
          type="button"
          name="manage"
          value="0"
          className={`option-button no ${formData.manage === '0' ? 'selected-no' : 'unselected'}`}
          onClick={() => {handleInputChange, setManage(false)}}
        >
          No
        </button>
      </div>


      <button className="save-button" disabled={!isFormValid}>
        Save and Proceed
      </button>

      {isLoading && (
        <TailSpin type="ThreeDots" color="#00BFFF" height={50} width={50} />
      )}

      {!isFormValid && isManage && (
        <p>Fill in the details</p>
      )}

      <p>{isProjectRegistered}</p>
    </form>
  );
};

export default Component1;
