import React, { useState } from 'react';
import './comp3.css';

const Component3 = (props) => {
  const { changeId } = props;
  const [selectedOption, setSelectedOption] = useState('');
  const [manage, setManage] = useState(null);

  const prevComponent = () => {
    changeId(2);
  };

  const nextComponent = () => {
    if (selectedOption === 'yes' || selectedOption === 'no') {
      changeId(4);
    }
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setManage(option === 'yes' ? 1 : 0);
  };

  return (
    <div className="component3">
      <h2>Create a project</h2>
      <br />
      <div className="button-group">
        <button
          className={`option-button ${selectedOption === 'yes' ? 'selected-yes' : 'yes'}`}
          onClick={() => handleOptionClick('yes')}
        >
          Yes
        </button>
        <button
          className={`option-button ${selectedOption === 'no' ? 'selected-no' : 'no'}`}
          onClick={() => handleOptionClick('no')}
        >
          No
        </button>
      </div>
      <button className="prev-button" onClick={prevComponent}>
        Previous
      </button>
      <button className="save-button" onClick={nextComponent} disabled={!selectedOption}>
        Save and Proceed
      </button>
      {manage !== null && <p>Manage value: {manage}</p>}
    </div>
  );
};

export default Component3;
