import React, { useState } from 'react';
import './comp1.css';

const Component1 = (props) => {
  const { changeId } = props;
  const [name, setName] = useState('');
  const [projectType, setProjectType] = useState('');
  const [projectRole, setProjectRole] = useState('');
  const [isFormValid, setIsFormValid] = useState(false);

  const changeComponent = (e) => {
    e.preventDefault();
    if (isFormValid) {
      changeId(2);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'name') {
      setName(value);
    } else if (name === 'projectType') {
      setProjectType(value);
    } else if (name === 'projectRole') {
      setProjectRole(value);
    }
  };

  // Validate the form whenever any input changes
  React.useEffect(() => {
    const form = document.getElementById('componentForm');
    setIsFormValid(form.checkValidity());
  }, [name, projectType, projectRole]);

  return (
    <form id="componentForm" className="component1" onChange={handleInputChange}>
      <h2>Create a project</h2>
      <br />
      <label>
        <h3>Name</h3>
        <input
          className="input-box"
          type="text"
          name="name"
          value={name}
          onChange={handleInputChange}
          required
        />
      </label>

      <label>
        <h3>Type</h3>
        <select
          className="select-box"
          name="projectType"
          value={projectType}
          onChange={handleInputChange}
          required
        >
          <option value="" disabled hidden>
            Select a type of project
          </option>
          <option value="User Application">User Application</option>
          <option value="SDK">SDK</option>
          <option value="Protocol Stack">Protocol Stack</option>
        </select>
      </label>

      <label>
        <h3>Select Your Role</h3>
        <select
          className="select-box"
          name="projectRole"
          value={projectRole}
          onChange={handleInputChange}
          required
        >
          <option value="" disabled hidden>
            Select your role
          </option>
          <option value="BAP">BAP</option>
          <option value="BPP">BPP</option>
          <option value="Beckn Gateway">Beckn Gateway</option>
          <option value="Beckn Registry">Beckn Registry</option>
        </select>
      </label>

      <button className="save-button" onClick={changeComponent} disabled={!isFormValid}>
        Save and Proceed
      </button>
    </form>
  );
};

export default Component1;
