import React, { useState } from 'react';
// import './apicomp.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';

const ApiComponent = (props) => {
    const { data } = props;
    const { project, funnel_stages, project_role, project_type } = data;
    const [clickName, setName] = useState('Copy')
    const [clicked, setClicked] = useState(false);

    const handleCopyClick = () => {
        const apiText = `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`;
        navigator.clipboard.writeText(apiText);
        setName('Copied')
        setClicked(true);
        setTimeout(() => {
            setClicked(false);
            setName('Copy')
        }, 1000); // Reset after 1 second
    };

    return (
        <div className="w-full h-full min-h-[50px] max-h-[70px] flex flex-row items-center bg-[lightblue] m-4 p-2 rounded-2xl">
            <h1 className='text-base'>{project}</h1>
            <div className={`w-[70%] flex ml-4 ${clicked ? 'clicked' : ''}`}>
                <h1 className='text-base text-[blueviolet]' onClick={handleCopyClick}>
                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
                    <button className="bg-[#4caf50] text-[white] text-center no-underline inline-block text-xs cursor-pointer text-base ml-[5px] px-2.5 py-[5px] rounded-[5px] border-[none]">{clickName}</button>
                </h1>
            </div>
            <div className='flex justify-evenly w-2/5 ml-auto'>
                <p className='ml-4'>{funnel_stages}</p>
                <p className='text-base'>{project_role}</p>
                <FontAwesomeIcon icon={faEdit} className='text-[darkblue] self-center' />
            </div>
        </div>
    );
};

export default ApiComponent;
