import React, { useState, useRef, useEffect } from 'react';
import './navbar.css';

const Navbar = (props) => {
  const {userData} = props 
  const{username} = userData
  const letter = username[0]
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  const handleDropdownClick = () => {
    setDropdownVisible(!dropdownVisible);
  };

  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownVisible(false);
      }
    };

    document.addEventListener('click', handleOutsideClick);

    return () => {
      document.removeEventListener('click', handleOutsideClick);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="navbar-heading">BIAB</div>
      <div className="navbar-search">
        <input type="text" placeholder="Search for people, communities, code and more..." />
      </div>
      <div className="navbar-profile">
        <div className="profile-photo"><p className='letter'>{letter}</p></div>
        <div
          className={`profile-dropdown ${dropdownVisible ? 'active' : ''}`}
          ref={dropdownRef}
          onClick={handleDropdownClick}
        >
          <span className="dropdown-icon">&#9776;</span>
          <div className="dropdown-content">
            <p>Name</p>
            <p>Email</p>
            <p>Logout</p>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
