import React, { useState, useEffect } from 'react';
import Component1 from './Component1/comp1';
import './wizard.css';
import { v4 as uuidv4 } from 'uuid';

function Wizard(props) {
  const { email } = props;
  const organization = "ONDC";
  const [activeComponent, setActiveComponent] = useState(1);
  const [formData, setFormData] = useState({});
  const [isProjectAdded, setIsProjectAdded] = useState(false); // Track if project is added

  useEffect(() => {
    if (isProjectAdded) {
      setFormData({});
      setIsProjectAdded(false);
      setActiveComponent(1);
    }
  }, [isProjectAdded]);

  const handleFormSubmit = (data) => {
    setFormData({ ...data });
  };

  const addProjectToDatabase = async () => {
    try {
      const request = { ...formData, id: uuidv4(), email: email };
      console.log(request);
      const response = await fetch('http://localhost:3001/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const responseData = await response.json();
      setIsProjectAdded(true); // Set the flag to indicate project added
      alert('Added Project Successfully');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className='wizard-container'>
      <h1 className="mc-heading">Build with Beckn</h1>
      <div>
        {activeComponent === 1 && <Component1 onSubmit={handleFormSubmit} org={organization} />}
      </div>
    </div>
  );
}

export default Wizard;
